import React, { useState } from 'react';
import { View, StyleSheet, Image, SafeAreaView, TouchableOpacity, ScrollView, TextInput } from 'react-native';
import LinearProgress from '@material-ui/core/LinearProgress';
import * as theme from "../../components/theme";
import { Block, Text } from "../../components";
import Button from '@material-ui/core/Button';

const CurrencyFormatting = ({ navigation }) => {
    const [formated, setFormated] = useState('');

    const validate = (currency) => {
        // let reg = "^\$\d{1,3}(,\d{3})*(\.\d+)?$"
        const formatter = new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 2
        })
        setFormated(formatter.format(currency));
    }

    return <View>
        <SafeAreaView style={styles.safe}>
            <Block flex={0.42} column style={{ paddingHorizontal: 15 }}>
                <Block flex={false} row style={{ paddingVertical: 15 }}>
                    <Block center>
                        <Text h3 white style={{ marginRight: -(25 + 5) }}>Currency Formatting</Text>
                    </Block>
                </Block>
                <Block row card shadow color="white" style={styles.headerChart}>
                    <Block style={{ paddingHorizontal: 30 }}>
                        <Block flex={false} row center>
                            <Text h2>Auto Formatting Currency</Text>
                        </Block>
                    </Block>
                    <Button size="small" color="primary" onClick={() => navigation.navigate('SoilSense')}>
                        Go to main page
        </Button>
                </Block>
            </Block>
            <Block flex={0.8} column color="gray2" style={styles.requests}>
                <ScrollView showsVerticalScrollIndicator={false}>
                    <Block card shadow color="white" style={styles.request}>
                        <TextInput
                            type="text"
                            name="currency-field"
                            id="currency-field"
                            data-type="currency"
                            placeholder="$1,000,000.00"
                            onChangeText={(currency) => validate(currency)}
                        />
                        <Text style={styles.forCurrency}>{formated}</Text>
                    </Block></ScrollView></Block>
        </SafeAreaView>
    </View>
}


const styles = StyleSheet.create({
    safe: {
        flex: 1,
        backgroundColor: theme.colors.primary
    },
    request: {
        padding: 20,
        marginBottom: 15
    },
    requests: {
        marginTop: -55,
        paddingTop: 55 + 20,
        paddingHorizontal: 15,
        zIndex: -1
    },
    headerChart: {
        paddingTop: 30,
        paddingBottom: 30,
        zIndex: 1,
    },
    forCurrency: {
        paddingTop: 30,
        paddingBottom: 30,
        fontWeight: 'bold'
    },
    progress: {
        alignItems: 'center',
        width: 50,
        justifyContent: 'center',
    }
})

export default CurrencyFormatting
