import React, { useState} from 'react'
import { Text, View, StyleSheet } from 'react-native'
import firebase from 'firebase'
import Button from '@material-ui/core/Button';
import 'firebase/firestore';


const Home = ({ navigation, route }) => {

  var config = {
    apiKey: 'AIzaSyBmMQzZDPK8WoRFsXZwEikglgPh8fdbRfU',
    authDomain: 'moonlit-premise-268014.firebaseapp.com',
    databaseURL: 'https://project-483449585526.firebaseio.com',
    projectId: 'moonlit-premise-268014',
  }
  
  if (!firebase.apps.length) {
    var app = firebase.initializeApp(config)
  }
  
  var db = firebase.firestore(app);
  
  var docRef = db.collection("soilSense").doc("D7S3VuyROx9nBNRG2XmF");
  

  const [arrData, setArrData] = useState([])
  // const h = () => {
  //    docRef.get().then(function(doc) {
  //     if (doc.exists) {
  //         console.log("Document data:", doc.data());
  //         console.log(doc)
  //     } else {
  //         // doc.data() will be undefined in this case
  //         console.log("No such document!");
  //     }
  // }).catch(function(error) {
  //     console.log("Error getting document:", error);
  // });
// }
  // const { name } = route.params;
  const h = () => {
    //load hospitals into hospitalsList
    const arr = []
    db.collection('soilSense').get()
        .then(snapshot => {
            snapshot.docs.forEach(arr => {
                let currentID = arr.id
                let appObj = { ...arr.data(), ['id']: currentID }
                arr.push(appObj)

                arr.push(arr.data())
        })
        setArrData(arr)
    })
}

  const onPressed = () => {
    firebase
      .auth()
      .signOut()
      .then(() => {
        navigation.navigate('SignIn')
      })
      .catch((error) => {
        console.log(error);
      })
  }

  return (
    <View style={styles.wrapper}>
        <View style={styles.container}>
        <Text style={styles.titleWrapper}>Welcome, {h()} {}!</Text>
          <View style={styles.buttonWrapper}>
            <Button onClick={() => {onPressed()}}>Sign out</Button>
          </View>
        </View>
     </View>
  )
}

const styles = StyleSheet.create({
  wrapper: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ecf0f1',
  },
  container: {
    display: 'flex',
    flexDirection: 'column',
    padding: 16,
    width: 400,
    height: 400,
  },
  titleWrapper: {
    display: 'flex',
    fontWeight: 'bold',
    fontSize: 32,
    justifyContent: 'center',
  },
  buttonWrapper: {
    display: 'flex',
    borderWidth: 1, 
    borderStyle: 'solid', 
    borderColor: '#1A1E3D',
    marginTop: 16,
  },
})

export default Home
