import React, { useState} from 'react';
import {  View, StyleSheet, Image, SafeAreaView, TouchableOpacity, ScrollView } from 'react-native';
import firebase from 'firebase'
import 'firebase/firestore';
import LinearProgress from '@material-ui/core/LinearProgress';
import * as theme from "../../components/theme";
import { Block, Text } from "../../components";
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const TemperatureLevel = () => {
    var config = {
        apiKey: 'AIzaSyBmMQzZDPK8WoRFsXZwEikglgPh8fdbRfU',
        authDomain: 'moonlit-premise-268014.firebaseapp.com',
        databaseURL: 'https://project-483449585526.firebaseio.com',
        projectId: 'moonlit-premise-268014',
      }
      
      if (!firebase.apps.length) {
        var app = firebase.initializeApp(config)
      }
      
      var db = firebase.firestore(app);
      
      var docRef = db.collection("soilSense").doc("D7S3VuyROx9nBNRG2XmF");

      const [temperatureLevel, setTemperatureLevel] = useState(0);
    
    const getTemperatureLevel = () => {
      docRef.get().then(function(doc) {
       if (doc.exists) {
           console.log("Document data:", doc.data());
           setTemperatureLevel(doc.get('temperatureLevel'));
       } else {
           console.log("No such document!");
       }
    }).catch(function(error) {
       console.log("Error getting document:", error);
    });
    }

    const getSteps = () => {
        return ['Select campaign settings', 'Create an ad group', 'Create an ad'];
      }
      
    const getStepContent = (step) => {
        switch (step) {
          case 0:
            return 'Select campaign settings...';
          case 1:
            return 'What is an ad group anyways?';
          case 2:
            return 'This is the bit I really care about!';
          default:
            return 'Unknown step';
        }
      }
    
      const [activeStep, setActiveStep] = useState(0);
      const [skipped, setSkipped] = useState(new Set());
      const steps = getSteps();

      const isStepOptional = (step) => {
        return step === 1;
      };
    
      const isStepSkipped = (step) => {
        return skipped.has(step);
      };

      const handleNext = () => {
        let newSkipped = skipped;
        if (isStepSkipped(activeStep)) {
          newSkipped = new Set(newSkipped.values());
          newSkipped.delete(activeStep);
        }
    
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
        setSkipped(newSkipped);
      };
    
      const handleBack = () => {
        setActiveStep((prevActiveStep) => prevActiveStep - 1);
      };
    
      const handleSkip = () => {
        if (!isStepOptional(activeStep)) {
          // You probably want to guard against something like this,
          // it should never occur unless someone's actively trying to break something.
          throw new Error("You can't skip a step that isn't optional.");
        }
    
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
        setSkipped((prevSkipped) => {
          const newSkipped = new Set(prevSkipped.values());
          newSkipped.add(activeStep);
          return newSkipped;
        });
      };
    
      const handleReset = () => {
        setActiveStep(0);
      };

    return <View>
    {getTemperatureLevel()}
    {temperatureLevel === 0 ? <View  style={styles.progress}><LinearProgress /></View>: (
    <SafeAreaView style={styles.safe}>
  <Block flex={0.42} column style={{ paddingHorizontal: 15 }}>
  <Block flex={false} style={{ paddingVertical: 15 }}>
  <Block center>
  <Text h3 white style={{ marginRight: -(25 + 5) }}>Temperature Level</Text>
  </Block>
        </Block>
        <Block card shadow color="white" style={styles.headerChart}>
          <Block style={{ paddingHorizontal: 30 }}>
            <Block flex={false} row center>
              <Text h2>{temperatureLevel}Â° C</Text>
            </Block>
          </Block>
        </Block>
      </Block>
    </SafeAreaView>)}
    

    <div className={classes.root}>
      <Stepper activeStep={activeStep}>
        {steps.map((label, index) => {
          const stepProps = {};
          const labelProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = <Typography variant="caption">Optional</Typography>;
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step key={label} {...stepProps}>
              <StepLabel {...labelProps}>{label}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>
              All steps completed - you&apos;re finished
            </Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </div>
        ) : (
          <div>
            <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography>
            <div>
              <Button disabled={activeStep === 0} onClick={handleBack} className={classes.button}>
                Back
              </Button>
              {isStepOptional(activeStep) && (
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleSkip}
                  className={classes.button}
                >
                  Skip
                </Button>
              )}

              <Button
                variant="contained"
                color="primary"
                onClick={handleNext}
                className={classes.button}
              >
                {activeStep === steps.length - 1 ? 'Finish' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>

    
    </View>
}

export default TemperatureLevel

const styles = StyleSheet.create({
    root: {
        width: '100%',
      },
      button: {
        marginRight: theme.spacing(1),
      },
      instructions: {
        marginTop: theme.spacing(1),
        marginBottom: theme.spacing(1),
      },
    safe: {
      flex: 1,
      backgroundColor: theme.colors.primary
    },
    headerChart: {
      paddingTop: 30,
      paddingBottom: 30,
      zIndex: 1,
      marginBottom: 30,
    },
    progress: {
        alignItems: 'center',
        width: 50,
        justifyContent: 'center',
    }
})
