import React from 'react'
import {  View, StyleSheet, Image, SafeAreaView, TouchableOpacity, ScrollView } from 'react-native'
import firebase from 'firebase'
import Button from '@material-ui/core/Button';
import 'firebase/firestore';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { user } from '../../components/mocks';
import { Block, Text } from "../../components";
import * as theme from "../../components/theme";
import WbIncandescentIcon from '@material-ui/icons/WbIncandescent';
import OpacityIcon from '@material-ui/icons/Opacity';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import ColorizeIcon from '@material-ui/icons/Colorize';
import WhatshotIcon from '@material-ui/icons/Whatshot';
import YoutubeSearchedForIcon from '@material-ui/icons/YoutubeSearchedFor';
import TemperatureLevel from '../Temperature';


const SoilSense = ({ navigation }) => {

  const onPressed = () => {
    firebase
      .auth()
      .signOut()
      .then(() => {
        navigation.navigate('SignIn')
      })
      .catch((error) => {
        console.log(error);
      })
  }

  const renderRequests = () => {
    return (
      <Block flex={0.8} column color="gray2" style={styles.requests}>
        <ScrollView showsVerticalScrollIndicator={false}>
        <Block card shadow color="white" style={styles.request}>
        <List component="nav" aria-label="main mailbox folders">
        <ListItem button onClick={() =>  navigation.navigate('Temperature')}>
          <ListItemIcon>
          <WhatshotIcon />
          </ListItemIcon>
          <ListItemText primary="Temperature level" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          <OpacityIcon />
          </ListItemIcon>
          <ListItemText primary="Max water value" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          <LocationOnIcon />
          </ListItemIcon>
          <ListItemText primary="Sensor location" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          <WbIncandescentIcon/>
          </ListItemIcon>
          <ListItemText primary='Lighting level' />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          <ColorizeIcon />
          </ListItemIcon>
          <ListItemText primary="Humidity level" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          <YoutubeSearchedForIcon />
          </ListItemIcon>
          <ListItemText primary="Water pump" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          <MonetizationOnIcon />
          </ListItemIcon>
          <ListItemText primary="Currency formatting" />
        </ListItem>
      </List>
      </Block>
      <Block card shadow color="white" style={styles.request}>
          <View style={styles.buttonWrapper}>
            <Button onClick={() => {onPressed()}}>Sign out</Button>
          </View>
        </Block>
        </ScrollView>
      </Block>
    );
  }

  return (<SafeAreaView style={styles.safe}>
  <Block flex={0.42} column style={{ paddingHorizontal: 15 }}>
  <Block flex={false} row style={{ paddingVertical: 15 }}>
  <Block center>
  <Text h3 white style={{ marginRight: -(25 + 5) }}>Data from my sensors</Text>
  </Block>
  <Image style={styles.avatar} source={user.avatar} />
        </Block>
        <Block card shadow color="white" style={styles.headerChart}>
          <Block row style={{ paddingHorizontal: 30 }}>
            <Block flex={false} row center>
              <Text h2>Welcome!</Text>
            </Block>
          </Block>
        </Block>
      </Block>
      {renderRequests()}
    </SafeAreaView>)
}

const styles = StyleSheet.create({
  safe: {
    flex: 1,
    backgroundColor: theme.colors.primary
  },
  request: {
    padding: 20,
    marginBottom: 15
  },
  titleWrapper: {
    display: 'flex',
    fontWeight: 'bold',
    fontSize: 32,
    justifyContent: 'center',
  },
  buttonWrapper: {
    display: 'flex',
    borderWidth: 1, 
    borderStyle: 'solid', 
    borderColor: '#1A1E3D',
  },
  headerChart: {
    paddingTop: 30,
    paddingBottom: 30,
    zIndex: 1
  },
  requests: {
    marginTop: -55,
    paddingTop: 55 + 20,
    paddingHorizontal: 15,
    zIndex: -1
  },
  avatar: {
    width: 25,
    height: 25,
    borderRadius: 25 / 2,
    marginRight: 5
  },
})

export default SoilSense
