import React, { useState} from 'react'
import {  View, StyleSheet, Image, SafeAreaView } from 'react-native'
import firebase from 'firebase'
import Button from '@material-ui/core/Button';
import 'firebase/firestore';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import { user, chart } from '../../components/mocks';
import { Block, Text } from "../../components";
import * as theme from "../../components/theme";


const SoilSense = ({ navigation }) => {

  var config = {
    apiKey: 'AIzaSyBmMQzZDPK8WoRFsXZwEikglgPh8fdbRfU',
    authDomain: 'moonlit-premise-268014.firebaseapp.com',
    databaseURL: 'https://project-483449585526.firebaseio.com',
    projectId: 'moonlit-premise-268014',
  }
  
  if (!firebase.apps.length) {
    var app = firebase.initializeApp(config)
  }
  
  var db = firebase.firestore(app);
  
  var docRef = db.collection("soilSense").doc("D7S3VuyROx9nBNRG2XmF");
  
  const [humidityLevel, setHumidityLevel] = useState(0);
  const [lightingLevel, setLightingLevel] = useState('');
  const [maxWaterValue, setMaxWaterValue] = useState(0);
  const [sensorLocation, setSensorLocation] = useState('');
  const [temperatureLevel, setTemperatureLevel] = useState(0);
  const [waterPump, setWaterPump] = useState('');
  
  const getHumidityLevel = () => {
     docRef.get().then(function(doc) {
      if (doc.exists) {
          console.log("Document data:", doc.data());
          setHumidityLevel(doc.get('humidityLevel'));
      } else {
          console.log("No such document!");
      }
  }).catch(function(error) {
      console.log("Error getting document:", error);
  });
}

const getLightingLevel = () => {
  docRef.get().then(function(doc) {
   if (doc.exists) {
       console.log("Document data:", doc.data());
       setLightingLevel(doc.get('lightingLevel'));
   } else {
       console.log("No such document!");
   }
}).catch(function(error) {
   console.log("Error getting document:", error);
});
}

const getMaxWaterValue = () => {
  docRef.get().then(function(doc) {
   if (doc.exists) {
       console.log("Document data:", doc.data());
       setMaxWaterValue(doc.get('maxWaterValue'));
   } else {
       console.log("No such document!");
   }
}).catch(function(error) {
   console.log("Error getting document:", error);
});
}

const getSensorLocation = () => {
  docRef.get().then(function(doc) {
   if (doc.exists) {
       console.log("Document data:", doc.data());
       setSensorLocation(doc.get('sensorLocation'));
   } else {
       console.log("No such document!");
   }
}).catch(function(error) {
   console.log("Error getting document:", error);
});
}

const getTemperatureLevel = () => {
  docRef.get().then(function(doc) {
   if (doc.exists) {
       console.log("Document data:", doc.data());
       setTemperatureLevel(doc.get('temperatureLevel'));
   } else {
       console.log("No such document!");
   }
}).catch(function(error) {
   console.log("Error getting document:", error);
});
}

const getWaterPump = () => {
  docRef.get().then(function(doc) {
   if (doc.exists) {
       console.log("Document data:", doc.data());
       setWaterPump(doc.get('waterPump'));
   } else {
       console.log("No such document!");
   }
}).catch(function(error) {
   console.log("Error getting document:", error);
});
}



  const onPressed = () => {
    firebase
      .auth()
      .signOut()
      .then(() => {
        navigation.navigate('SignIn')
      })
      .catch((error) => {
        console.log(error);
      })
  }

  const renderChart = (props) => {
    const { chart } = props;
    const LineShadow = ({ line }) => (
      <Path
        d={line}
        fill="none"
        stroke={theme.colors.primary}
        strokeWidth={7}
        strokeOpacity={0.07}
      />
    );

    return (
      <LineChart
        yMin={0}
        yMax={10}
        data={chart}
        style={{ flex: 2 }}
        curve={shape.curveMonotoneX}
        svg={{
          stroke: theme.colors.primary,
          strokeWidth: 1.25
        }}
        contentInset={{ left: theme.sizes.base, right: theme.sizes.base }}
      >
        <LineShadow belowChart={true} />
        <Line
          key="zero-axis"
          x1="0%"
          x2="100%"
          y1="50%"
          y2="50%"
          belowChart={true}
          stroke={theme.colors.gray}
          strokeDasharray={[2, 10]}
          strokeWidth={1}
        />
      </LineChart>
    );
  }

  return (
    <View style={styles.wrapper}>
        <View style={styles.container}>
        {/* <Text style={styles.titleWrapper}>Welcome, {getHumidityLevel(), humidityLevel}!</Text> */}
        <SafeAreaView style={styles.safe}>
        <Block flex={0.42} column style={{ paddingHorizontal: 15 }}>
        <Block flex={false} row style={{ paddingVertical: 15 }}>
          <Block center>
            <Text h3 white style={{ marginRight: -(25 + 5) }}>
            Data from my sensors
            </Text>
          </Block>
          <Image style={styles.avatar} source={user.avatar} />
        </Block>
        <Block card shadow color="white" style={styles.headerChart}>
          <Block row style={{ paddingHorizontal: 30 }}>
            <Block flex={false} row center>
            <Text style={styles.titleWrapper}>Welcome, {getHumidityLevel(), humidityLevel}!</Text>
            </Block>
            <Block flex={1}>{renderChart()}</Block>
          </Block>
        </Block>
      </Block>
      </SafeAreaView>
        <List component="nav" aria-label="main mailbox folders">
        <ListItem button>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Temperature level" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Max water value" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Sensor location" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary='Lighting level' />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Humidity level" />
        </ListItem>
        <Divider/>
        <ListItem button>
          <ListItemIcon>
          </ListItemIcon>
          <ListItemText primary="Water pump" />
        </ListItem>
      </List>
          <View style={styles.buttonWrapper}>
            <Button onClick={() => {onPressed()}}>Sign out</Button>
          </View>
        </View>
     </View>
  )
}

const styles = StyleSheet.create({
  wrapper: {
    width: '100%',
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ecf0f1',
  },
  safe: {
    flex: 1,
    backgroundColor: theme.colors.primary
  },
  container: {
    display: 'flex',
    flexDirection: 'column',
    padding: 16,
    width: 360,
    height: 400,
  },
  titleWrapper: {
    display: 'flex',
    fontWeight: 'bold',
    fontSize: 32,
    justifyContent: 'center',
  },
  buttonWrapper: {
    display: 'flex',
    borderWidth: 1, 
    borderStyle: 'solid', 
    borderColor: '#1A1E3D',
    marginTop: 16,
  },
  headerChart: {
    paddingTop: 30,
    paddingBottom: 30,
    zIndex: 1
  },
  avatar: {
    width: 25,
    height: 25,
    borderRadius: 25 / 2,
    marginRight: 5
  },
})

export default SoilSense
